import "./App.css";
import { useState, useEffect } from "react";
import MyLibrary from "./components/MyLibrary";
import SearchBooks from "./components/SearchBooks";
import { Route, Routes } from "react-router-dom";
import * as BooksAPI from "./BooksAPI.js"

function App() {

  const shelves = 
    [
      { id: 1, category: "currentlyReading"},
      { id: 2, category: "wantToRead"},
      { id: 3, category: "read"}
    ];

  const [books, setBooks] = useState ([]);

  const updateBookShelf = useCallback(async (book, shelf) => {
    await BooksAPI.update(book, shelf);
    setBooks((currentBooks) =>
      currentBooks.map((b) => {
        if (b.id === book.id) {
          return { ...b, shelf };
        } else {
          return b;
        }
      })
    );
  }, []);

  useEffect(() => {
    const getBooks = async() => {
      const res = await BooksAPI.getAll();
      setBooks(res);
    }
    getBooks();
  }, [])

  return (
    <Routes>
        <Route exact path="/" element={<MyLibrary shelves={shelves} books={books} updateBookShelf={updateBookShelf} ></MyLibrary>} />
        <Route exact path="/search" element={<SearchBooks shelves={shelves} books={books}></SearchBooks>} />
    </Routes>
  );
}

export default App;
