import Book from "./Book";
import { useState } from "react";

const Shelf = ( {shelf, shelves, books} )=> {

    const [ shelfStatus, setshelfStatus ] = useState(shelf)

    const hanldeShelfChange = (shelf_to_update) =>
    {
        setshelfStatus(shelf_to_update);
    }

    return (
        <div className="bookshelf">
                <h2 className="bookshelf-title">{shelf.category}</h2>
                <div className="bookshelf-books">
                    <ol className="books-grid">
                    {
                        books.map((book) => ( 
                            book.shelf === shelfStatus.category 
                            ? <li key={book.name}><Book shelves={shelves} book={book} hanldeShelfChange={hanldeShelfChange} ></Book></li>
                            : null
                        ))
                    }
                    </ol>
                </div>
        </div>
    );


}

export default Shelf;

