import { useState } from "react";

const Book = ( {book, shelves, updateBookShelf} ) => {

    const [bookShelf, setBookShelf, ] = useState(book.shelf || "none")

    const handleShelfChange = async (e) => {
        const newShelf = e.target.value;
        setBookShelf(newShelf);
        await updateBookShelf(book, newShelf);
      };

    return (
        <li>
            <div className="book">
                <div className="book-top">
                    <div
                    className="book-cover"
                    style={{
                        width: 128,
                        height: 193,
                        backgroundImage:
                        `url(${book.imageLinks.smallThumbnail})`,
                    }}
                    ></div>
                    <div className="book-shelf-changer">
                    <select value={bookShelf} onChange={handleShelfChange}>
                        <option value="none" disabled>
                        Move to...
                        </option>
                        {
                            shelves.map((shelf)=> (
                                <option key={shelf.id} value={shelf.category}>{shelf.category}</option>
                            ))
                        }
                        <option value="none">None</option>
                    </select>
                    </div>
                </div>
                <div className="book-title">{book.title}</div>
                <div className="book-authors">{book.authors}</div>
            </div>
        </li>

    )

}

export default Book;
